// Tính toán ngày hôm qua
const yesterday = new Date();
yesterday.setDate(yesterday.getDate() - 1);

// Format ngày theo định dạng YYYY-MM-DD
const yesterdayStr = yesterday.toISOString().split('T')[0];

// TikTok profile URL
const profileUrl = 'https://www.tiktok.com/@dau_dau480';

return [{
  json: {
    profileUrl: profileUrl,
    targetDate: yesterdayStr,
    timestamp: new Date().toISOString()
  }
}];

// Lưu ý: TikTok sử dụng JavaScript để load nội dung động
// Phương pháp này có thể không hoạt động hoàn hảo do TikTok chống scraping

const html = $input.first().json.data;
const targetDate = $('Calculate Yesterday Date').first().json.targetDate;

// Tìm kiếm các script tags chứa dữ liệu JSON
const scriptMatches = html.match(/<script[^>]*>window\.__INITIAL_STATE__\s*=\s*({.*?});?<\/script>/s);

let posts = [];

if (scriptMatches && scriptMatches[1]) {
  try {
    const initialState = JSON.parse(scriptMatches[1]);

    // Tìm dữ liệu video trong cấu trúc JSON
    // Cấu trúc có thể thay đổi theo thời gian
    if (initialState.ItemList && initialState.ItemList.user && initialState.ItemList.user.list) {
      const videoList = initialState.ItemList.user.list;

      videoList.forEach(video => {
        if (video && video.createTime) {
          const createDate = new Date(video.createTime * 1000);
          const videoDate = createDate.toISOString().split('T')[0];

          // Chỉ lấy video của ngày hôm qua
          if (videoDate === targetDate) {
            posts.push({
              id: video.id,
              desc: video.desc || '',
              createTime: createDate.toISOString(),
              url: `https://www.tiktok.com/@dau_dau480/video/${video.id}`,
              stats: {
                playCount: video.stats?.playCount || 0,
                diggCount: video.stats?.diggCount || 0,
                shareCount: video.stats?.shareCount || 0,
                commentCount: video.stats?.commentCount || 0
              },
              music: video.music?.title || '',
              hashtags: video.textExtra?.filter(item => item.hashtagName).map(item => item.hashtagName) || []
            });
          }
        }
      });
    }
  } catch (error) {
    console.log('Error parsing TikTok data:', error.message);
  }
}

// Nếu không tìm thấy dữ liệu, tạo entry trống để ghi log
if (posts.length === 0) {
  posts.push({
    id: 'NO_DATA',
    desc: 'Không tìm thấy bài đăng nào cho ngày ' + targetDate,
    createTime: new Date().toISOString(),
    url: 'N/A',
    stats: { playCount: 0, diggCount: 0, shareCount: 0, commentCount: 0 },
    music: '',
    hashtags: []
  });
}

return posts.map(post => ({ json: post }));
