// Lấy item đầu tiên (hoặc lặp nếu nhiều item)
const items = [];

for (const item of $input.all()) {
  const duration = item.json.data?.duration;

  let videoType = 'Không xác định';

  if (duration > 90) {
    videoType = 'Video Watch';
  } else {
    videoType = 'Video Reels';
  }

  items.push({
    json: {
      duration,
      videoType,
    }
  });
}

return items;

let count = 0;
let total = items.length;

for (const item of items) {
  const status = item.json["Trạng Thái Đăng"];

  // Kiểm tra xem status có phải kiểu string không trước khi gọi toLowerCase
  if (typeof status === "string" && status.toLowerCase().trim() === "chưa đăng") {
    count++;
  }
}

return [
  {
    json: {
      total: total,
      chua_dang: count
    }
  }
];

